function getURLParameter(a){return decodeURI((RegExp("[?|&]"+a+"=(.+?)(&|$)").exec(location.search)||[,null])[1])}function insertParam(a,b){kpv=addURLParameter(document.location.search.substr(1),[[a,b]]);document.location.search=kpv}function addURLParameter(d,b){var c=d.split("&");var e=c.length;var a;for(var f=0;f<b.length;f++){e=c.length;key=escape(b[f][0]);value=escape(b[f][1]);while(e--){a=c[e].split("=");if(a[0]==key){a[1]=value;c[e]=a.join("=");break}}if(e<0){c[c.length]=[key,value].join("=")}}return c.join("&")};